import java.util.*;
class Solution {
  static int findPlatform(int arr[], int dep[]) {
    Arrays.sort(arr);
    Arrays.sort(dep);
    int platforms = 1, maxPlatforms = 1;
    int i = 1, j = 0, n = arr.length;
    while (i < n && j < n) {
      if (arr[i] <= dep[j]) {
        platforms++;
        maxPlatforms = Math.max(maxPlatforms, platforms);
        i++;
      } else {
        platforms--;
        j++;
      }
    }
    return maxPlatforms;
  }
}

