code.java:
class Solution {
  public int[] countBits(int n) {
    int[] ans = new int[n + 1];
    for (int i = 0; i <= n; i++) {
      ans[i] = Integer.bitCount(i); // Built-in bit count
    }
    return ans;
  }
}

code.cpp:
#include <vector>
#include <bitset>
class Solution {
public:
  std::vector<int> countBits(int n) {
    std::vector<int> ans(n + 1);
    for (int i = 0; i <= n; ++i) {
      ans[i] = std::bitset<32>(i).count(); // Count 1's in binary
    }
    return ans;
  }
};

code.java:
class Solution {
  public int[] countBits(int n) {
    int[] dp = new int[n + 1];
    for (int i = 1; i <= n; i++) {
      dp[i] = dp[i >> 1] + (i & 1); // i / 2 + last bit
    }
    return dp;
  }
}

code.cpp:
#include <vector>
class Solution {
public:
  std::vector<int> countBits(int n) {
    std::vector<int> dp(n + 1, 0);
    for (int i = 1; i <= n; ++i) {
      dp[i] = dp[i >> 1] + (i & 1); // i / 2 + last bit
    }
    return dp;
  }
};
