code.java:
import java.util.Arrays;
class Solution {
  public int missingNumber(int[] nums) {
    Arrays.sort(nums);
    int n = nums.length;
    for (int i = 0; i < n; i++) {
      if (nums[i] != i) return i;
    }
    return n;
  }
}

code.cpp:
#include <vector>
#include <algorithm>
class Solution {
public:
  int missingNumber(std::vector<int>& nums) {
    std::sort(nums.begin(), nums.end());
    int n = nums.size();
    for (int i = 0; i < n; i++) {
      if (nums[i] != i) return i;
    }
    return n;
  }
}

code.java:
class Solution {
  public int missingNumber(int[] nums) {
    int n = nums.length;
    int expectedSum = n * (n + 1) / 2;
    int actualSum = 0;
    for (int num : nums) actualSum += num;
    return expectedSum - actualSum;
  }
}

code.cpp:
#include <vector>
#include <numeric>
class Solution {
public:
  int missingNumber(std::vector<int>& nums) {
    int n = nums.size();
    int expectedSum = n * (n + 1) / 2;
    int actualSum = std::accumulate(nums.begin(), nums.end(), 0);
    return expectedSum - actualSum;
  }
}

code.java:
class Solution {
  public int missingNumber(int[] nums) {
    int xorResult = 0;
    int n = nums.length;
    for (int i = 0; i <= n; i++) xorResult ^= i;
    for (int num : nums) xorResult ^= num;
    return xorResult;
  }
}

