import java.util.HashSet;
public class Solution {
  public ListNode getIntersectionNode(ListNode headA, ListNode headB) {
    HashSet<ListNode> visited = new HashSet<>();
    ListNode curr = headA;
    while (curr != null) {
      visited.add(curr);
      curr = curr.next;
    }
    curr = headB;
    while (curr != null) {
      if (visited.contains(curr)) return curr;
      curr = curr.next;
    }
    return null;
  }
}

