#include <unordered_set>
class Solution {
public:
  ListNode *getIntersectionNode(ListNode *headA, ListNode *headB) {
    std::unordered_set<ListNode*> visited;
    ListNode* curr = headA;
    while (curr != nullptr) {
      visited.insert(curr);
      curr = curr->next;
    }
    curr = headB;
    while (curr != nullptr) {
      if (visited.find(curr) != visited.end()) return curr;
      curr = curr->next;
    }
    return nullptr;
  }
};

