class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if (head == null || head.next == null || k == 0) {
            return head;
        }
        int length = 1;
        ListNode tail = head;
        while (tail.next != null) {
            tail = tail.next;
            length++;
        }
        k %= length;
        if (k == 0) return head;
        for (int i = 0; i < k; i++) {
            ListNode curr = head;
            while (curr.next != tail) {
                curr = curr.next;
            }
            tail.next = head;
            head = tail;
            tail = curr;
            tail.next = null;
        }
        return head;
    }
}

