import java.util.ArrayList;
import java.util.Collections;
class Solution {
    public int kthSmallest(int[][] matrix, int k) {
        ArrayList<Integer> flattened = new ArrayList<>();
        for (int[] row : matrix) {
            for (int num : row) {
                flattened.add(num);
            }
        }
        Collections.sort(flattened);
        return flattened.get(k - 1);
    }
}

