class Solution {
  public boolean isAnagram(String s, String t) {
    if (s.length() != t.length()) return false;
    int[] freq = new int[26];
    for (int i = 0; i < s.length(); i++) {
      freq[s.charAt(i) - 'a']++;
      freq[t.charAt(i) - 'a']--;
    }
    for (int count : freq) {
      if (count != 0) return false;
    }
    return true;
  }
}

code.cpp:
#include <vector>
#include <string>
class Solution {
public:
  bool isAnagram(std::string s, std::string t) {
    if (s.length() != t.length()) return false;
    std::vector<int> freq(26, 0);
    for (int i = 0; i < s.length(); i++) {
      freq[s[i] - 'a']++;
      freq[t[i] - 'a']--;
    }
    for (int count : freq) {
      if (count != 0) return false;
    }
    return true;
  }
}
